function assemble(program) {
  const instructions = [];
  for (const line of program) {
    const parts = line.split('#')[0].trim().split(/\s+/);
    if (parts.length === 0) continue;
    const opcode = parts[0];
    const arg = parseInt(parts[1]) || 0;
    let command_byte;
    switch (opcode) {
      case 'CONTROL':
        command_byte = 0;
        break;
      case 'COND':
        command_byte = 128;
        break;
      case 'LOAD':
        command_byte = 4;
        break;
      case 'SWP':
        command_byte = 8;
        break;
      case 'PUSH':
        command_byte = 32;
        break;
      case 'POP':
        command_byte = 33;
        break;
      default:
        throw new Error(`Unknown opcode: ${opcode}`);
    }
    instructions.push(command_byte);
    instructions.push(arg < 0 ? (256 + arg) & 0xFF : arg & 0xFF);
  }
  return instructions;
}
