    const parts = line.split(/[\s#]+/).filter(part => part.trim() !== '');
    const opcode = parts[0].toUpperCase();
    const arg = parseInt(parts[1]) || 0;
    const cmd_byte = opcodes[opcode];
    const arg_byte = arg < 0 ? (256 + arg) : arg;
    instructions.push(cmd_byte, arg_byte);
  }

  return instructions;
}
