        flat_obj = {}
        for key, value in obj.items():
            if isinstance(value, (dict, list)):
                flat_obj.update(flatten(value))
            else:
                flat_obj[key] = value
        return flat_obj
    elif isinstance(obj, list):
        flat_obj = []
        for i, value in enumerate(obj):
            if isinstance(value, (dict, list)):
                flat_obj.extend(flatten(value))
            else:
                flat_obj.append(value)
        return flat_obj
    else:
        return obj
