function flatten(obj) {
  const flatObj = {};

  for (const key in obj) {
    if (typeof obj[key] === 'object') {
      // Merge object into parent
      Object.assign(flatObj, flatten(obj[key]));
    } else if (Array.isArray(obj[key])) {
      // Merge list into parent
      obj[key].forEach((item, index) => {
        flatObj[`${key}.${index}`] = item;
      });
    } else {
      // Leave simple types unmodified
      flatObj[key] = obj[key];
    }
  }

  return flatObj;
}
