def flatten(obj):
    flat_obj = {}
    for key, value in obj.items():
        if isinstance(value, dict):
            flat_obj.update({key + "." + k: v for k, v in flatten(value).items()})
        elif isinstance(value, list):
            for i, item in enumerate(value):
                flat_obj.update({key + "." + str(i): item})
        else:
            flat_obj[key] = value
    return flat_obj
