def assemble(program: list[str]) -> list[int]:
    opcode_map = {
        "CONTROL": 0,
        "COND": 128,
        "LOAD": 4,
        "SWP": 8,
        "PUSH": 32,
        "POP": 33,
    }

    assembled_program = []
    for line in program:
        opcode, *args = line.split()
        opcode_byte = opcode_map[opcode]
        arg_byte = 0

        if args:
            arg_byte = int(args[0])
            if arg_byte < 0:
                arg_byte = arg_byte & 255

        assembled_program.extend([opcode_byte, arg_byte])

    return assembled_program

