    };

    const assembled = [];
    for (const line of program) {
        const [opcode, arg] = line.split(/\s+/).slice(0, 2);
        const commandByte = OPCODE[opcode];
        const argumentByte = parseInt(arg) & 0xFF;
        assembled.push(...instructions[commandByte](argumentByte));
    }
    return assembled;
}
