    for line in program:
        # Remove comments
        line = line.split('#')[0].strip()
        parts = line.split()
        if not parts:
            continue  # Skip empty lines

        opcode = parts[0].upper()
        argument = 0
        if len(parts) > 1:
            try:
                argument = int(parts[1])
            except ValueError:
                raise ValueError(f"Invalid argument: {parts[1]}")

        # Convert negative arguments to two's complement
        if argument < 0:
            argument = argument & 0xFF

        command_byte = {
            "CONTROL": 0,
            "COND": 128,
            "LOAD": 4,
            "SWP": 8,
            "PUSH": 32,
            "POP": 33,
        }.get(opcode)

        if command_byte is None:
            raise ValueError(f"Invalid opcode: {opcode}")

        bytecode.extend([command_byte, argument])

    return bytecode

