function assemble(program) {
  const OPCODES = {
    CONTROL: 0,
    COND: 128,
    LOAD: 4,
    SWP: 8,
    PUSH: 32,
    POP: 33,
  };

  const bytecode = [];

  for (const line of program) {
    const [instruction, ...rest] = line.split(' ');
    const commentIndex = rest.findIndex(part => part.startsWith('#'));
    const argument = commentIndex === -1 ? parseInt(rest.join(' ') || 0) : parseInt(rest.slice(0, commentIndex).join(' ') || 0);

    // Handle negative arguments using two's complement
    if (argument < 0) {
      argument = (argument + 256) % 256;
    }

    const commandByte = OPCODES[instruction];

    bytecode.push(commandByte);
    bytecode.push(argument);
  }

  return bytecode;
}
