        const [opcode, arg, comment] = line.split(/[ \t]+/);
        const commandByte = OPCODES[opcode];
        let argumentByte = parseInt(arg || 0);

        if (commandByte === undefined) {
            throw new Error(`Invalid opcode: ${opcode}`);
        }

        if (argumentByte < 0) {
            argumentByte = 256 + argumentByte;
        }

        instructions.push(commandByte, argumentByte);
    }

    return instructions;
}
