function assemble(program) {
    const opcode = {
        CONTROL: 0,
        COND: 128,
        LOAD: 4,
        SWP: 8,
        PUSH: 32,
        POP: 33
    };

    let instructions = [];

    for (let line of program) {
        let tokens = line.split(' ');
        if (tokens.length < 2) continue;

        let instruction = tokens[0];
        let argument = parseInt(tokens[1]);

        switch (instruction) {
            case "CONTROL":
                instructions.push(opcode.CONTROL);
