function flatten(obj) {
  var flat = {};
  for (var key in obj) {
    if (typeof obj[key] === 'object' && !Array.isArray(obj[key])) {
      Object.assign(flat, flatten(obj[key]));
    } else if (Array.isArray(obj[key])) {
      for (var i = 0; i < obj[key].length; i++) {
        flat['.' + key + '.' + i] = obj[key][i];
      }
    } else {
      flat[key] = obj[key];
    }
  }
  return flat;
}
