function flatten(obj) {
  if (typeof obj !== 'object') {
    return obj;
  }
  let result = {};
  for (let key in obj) {
    if (typeof obj[key] === 'object') {
      if (Array.isArray(obj[key])) {
        for (let i = 0; i < obj[key].length; i++) {
          result[key + '.' + i] = flatten(obj[key][i]);
        }
      } else {
        result = {...result, ...flatten(obj[key])};
      }
    } else {
      result[key] = obj[key];
    }
  }
  return result;
}
```

