    for line in program:
        # Split the line into OPCODE, argument, and comment
        parts = line.split()
        opcode = parts[0]
        arg = 0
        if len(parts) > 1:
            arg = int(parts[1])

        # Handle comments
        if "#" in line:
            line = line.split("#", 1)[0]

        # Check for valid OPCODEs
        if opcode == "CONTROL":
            command_byte = 0
        elif opcode == "COND":
            command_byte = 128
        elif opcode == "LOAD":
            command_byte = 4
        elif opcode == "SWP":
            command_byte = 8
        elif opcode == "PUSH":
            command_byte = 32
        elif opcode == "POP":
            command_byte = 33
        else:
            raise ValueError(f"Invalid OPCODE: {opcode}")

        # Convert negative argument values to twos complement
        if arg < 0:
            arg = 256 + abs(arg)

        # Create instruction bytes
        byte_instructions.append(command_byte)
        byte_instructions.append(arg)

    return byte_instructions
